function initMobileEnhancements(){window.innerWidth<=768&&document.body.classList.add("mobile-view"),window.addEventListener("resize",function(){window.innerWidth<=768?document.body.classList.add("mobile-view"):document.body.classList.remove("mobile-view")}),initTableScrollIndicators(),enhanceTooltipsForMobile(),addTouchTargets(),console.log("Mobile enhancements initialized")}function initTableScrollIndicators(){document.querySelectorAll(".overflow-auto").forEach(e=>{let t=e.querySelector("table");t&&(e.addEventListener("scroll",function(){e.scrollLeft+e.clientWidth>=t.offsetWidth-10?e.classList.add("scrolled-to-end"):e.classList.remove("scrolled-to-end")}),t.offsetWidth>e.clientWidth?e.classList.add("is-scrollable"):e.classList.add("not-scrollable"))})}function enhanceTooltipsForMobile(){document.querySelectorAll(".tooltip").forEach(e=>{var t=e.querySelector(".tooltip-icon");let o=e.querySelector(".tooltip-text");t&&o&&t.addEventListener("click",function(e){e.preventDefault(),e.stopPropagation(),document.querySelectorAll(".tooltip-active").forEach(e=>{e!==o&&e.classList.remove("tooltip-active")}),o.classList.toggle("tooltip-active")})}),document.addEventListener("click",function(){document.querySelectorAll(".tooltip-active").forEach(e=>{e.classList.remove("tooltip-active")})})}function addTouchTargets(){document.querySelectorAll("button.delete").forEach(e=>{e.classList.contains("touch-target")||e.classList.add("touch-target")})}document.addEventListener("DOMContentLoaded",function(){initMobileEnhancements()}),window.initMobileEnhancements=initMobileEnhancements,window.initTableScrollIndicators=initTableScrollIndicators,window.enhanceTooltipsForMobile=enhanceTooltipsForMobile;