class NetflixOptionCalculator{constructor(){this.initialInvestment=1e3,this.currentStockPrice=500,this.optionDiscount=.4,this.exercisePrice=this.currentStockPrice,this.yearsToProject=10,this.annualGrowthRate=.1,this.taxRate=.33}setParameters(t,e,a,i,r,c){this.initialInvestment=t||this.initialInvestment,this.currentStockPrice=e||this.currentStockPrice,this.optionDiscount=a||this.optionDiscount,this.exercisePrice=this.currentStockPrice,this.yearsToProject=i||this.yearsToProject,this.annualGrowthRate=r||this.annualGrowthRate,this.taxRate=c||this.taxRate}getShareCount(){return this.initialInvestment/this.currentStockPrice}getOptionCount(){var t=this.currentStockPrice*this.optionDiscount;return this.initialInvestment/t}calculateStockValue(t){return this.getShareCount()*t}calculateOptionValue(t){return this.getOptionCount()*Math.max(0,t-this.exercisePrice)}calculateStockReturn(t){var e=this.initialInvestment;return(this.calculateStockValue(t)-e)/e}calculateOptionReturn(t){var e=this.initialInvestment;return(this.calculateOptionValue(t)-e)/e}calculateBreakEvenPrice(){return 1.4*this.exercisePrice}calculateOutperformPrice(){return this.exercisePrice*(1+2/3)}generatePriceComparisonData(){var e=.4*this.currentStockPrice,a=2.4*this.currentStockPrice,i=(a-e)/20,r=[];for(let t=e;t<=a;t+=i)r.push({stockPrice:t,stockValue:this.calculateStockValue(t),optionValue:this.calculateOptionValue(t)});return r}generateReturnComparisonData(){var e=.4*this.currentStockPrice,a=2.4*this.currentStockPrice,i=(a-e)/20,r=[];for(let t=e;t<=a;t+=i)r.push({stockPrice:t,stockReturn:100*this.calculateStockReturn(t),optionReturn:100*this.calculateOptionReturn(t)});return r}generateTimeProjectionData(){var e=[];for(let t=0;t<=this.yearsToProject;t++){var a=this.currentStockPrice*Math.pow(1+this.annualGrowthRate,t);e.push({year:t,stockValue:this.calculateStockValue(a),optionValue:this.calculateOptionValue(a)})}return e}calculateAfterTaxValues(t){var e=this.calculateStockValue(t),t=this.calculateOptionValue(t),a=e-this.initialInvestment,i=t-this.initialInvestment;return{afterTaxStockValue:e-Math.max(0,a*this.taxRate),afterTaxOptionValue:t-Math.max(0,i*this.taxRate)}}generateAfterTaxTimeProjection(){var e=[];for(let t=0;t<=this.yearsToProject;t++){var a=this.currentStockPrice*Math.pow(1+this.annualGrowthRate,t),{afterTaxStockValue:a,afterTaxOptionValue:i}=this.calculateAfterTaxValues(a);e.push({year:t,afterTaxStockValue:a,afterTaxOptionValue:i})}return e}}"undefined"!=typeof module&&void 0!==module.exports?module.exports=NetflixOptionCalculator:window.NetflixOptionCalculator=NetflixOptionCalculator;